DROP TABLE IF EXISTS `activities`;
CREATE TABLE `activities` (
  `Id` bigint(20) NOT NULL AUTO_INCREMENT,
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `last_modify_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  `name` varchar(255) NOT NULL DEFAULT '' COMMENT '名称',
  `place` varchar(255) DEFAULT '' COMMENT '活动地点',
  `semester_name` varchar(255) DEFAULT '' COMMENT '活动学期',
  `sponsor_mass` varchar(255) DEFAULT NULL COMMENT '主办社团',
  `hold_time` datetime DEFAULT '2000-01-01 12:00:00' COMMENT '举办时间',
  `type` varchar(255) DEFAULT '' COMMENT '活动类型',
  `review` int(11) DEFAULT '0' COMMENT '审核次数',
  `description` varchar(255) DEFAULT '' COMMENT '活动摘要',
  `join_people` int(11) unsigned DEFAULT '0' COMMENT '参加人数',
  `audience` int(11) unsigned DEFAULT '0' COMMENT '观众人数',
  `status` int(11) unsigned DEFAULT '0' COMMENT '状态',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 COMMENT='活动';

